    var timeNA = ordem.timeNaoApontada;
    var timeSS = ordem.timeScheduledStop;
    var timeUS = ordem.timeUnscheduledStop;
    var timeSM = ordem.timeStopMachine;

    timeNA[hour] = timeSM[hour] - (timeSS[hour] + timeUS[hour]);
    msg.payload = timeNA;
    return msg;
}

var process_id = global.get("process_id");
if (process_id !== 0 && process_id !== 900){
    var id = global.get("ordematual_id") || [];
    var rev = global.get("ordematual_rev") || [];
    var ordem = global.get("ordematual") || [];
    msg = {
        "payload":{
            "_id": id,
            "_rev": rev,
            "payload": ordem
        }
    }
    return msg;
}

var process_id = global.get("process_id") || [];
if (process_id !== 0 && process_id !== 900){
    var ordem = global.get("ordematual") || [];
    msg = {
        "payload":{
            "_id": ordem.ordem
        }
    }
    return msg;
}

var velocidadeAmort = flow.get("velocidadeAmort") || [];
var interval = msg.payload;
var velo = 60000 / interval;
velocidadeAmort[9] = velocidadeAmort[8];
velocidadeAmort[8] = velocidadeAmort[7];
velocidadeAmort[7] = velocidadeAmort[6];
velocidadeAmort[6] = velocidadeAmort[5];
velocidadeAmort[5] = velocidadeAmort[4];
velocidadeAmort[4] = velocidadeAmort[3];
velocidadeAmort[3] = velocidadeAmort[2];
velocidadeAmort[2] = velocidadeAmort[1];
velocidadeAmort[1] = velocidadeAmort[0];
velocidadeAmort[0] = velo;
flow.set("velocidadeAmort",velocidadeAmort);
var velocidade = velocidadeAmort.reduce(function(anterior, atual) {
  return anterior + atual;
});
msg.payload = parseInt((velocidade/10));
return msg;
