// get global variables
var unit = global.get("site").unit.h;
var colormap = global.get("site").widgetcolors;
// declare local variables
var c;
var colors = [];

// how many stripes should be drawn according to calculated widget size
var count = Math.floor(unit * msg.units / 4) - 2; 

// parameters for range incoming values into size of widget
var params = {minin:msg.min, maxin:msg.max+1, minout:0, maxout:count};

// range
function range(n,params){
 var divisor = params.maxin - params.minin;
 n = ((n - params.minin) % divisor + divisor) % divisor + params.minin;
 n = ((n - params.minin) / (params.maxin - params.minin) * (params.maxout - params.minout)) + params.minout;
 return n;
}
var rangedvalue = Math.round(range(msg.value,params));

// determine color for each stripe
for(var i=0; i<count; i++){
 c = rangedvalue <= i ? colormap.off : ( i < (count-5) ? colormap.on : ( i < (count-2) ? colormap.warn : colormap.high));
 colors.push(c);
}
//vertical starts drawing from top. colors needed to be in reverse order 
if(msg.direction === "vertical"){
 colors.reverse();
}

// set msg parameters
msg.colors = colors;

// for vertical, the last stripe will not be at the bottom exactly.
// for adjusting min value position.
msg.minpos = count*4; 
return msg;

